        user_id = user_row[0]

        # Step 5: Update the company country in the database
        cursor.execute(
            "UPDATE company_profiles SET company_country = %s, updated_at = NOW() WHERE user_id = %s",
            (selected_country, user_id)
        )
        conn.commit()

