            if phone_number and not phone_number.startswith("+"):
                phone_number = f"+{phone_number}"

            phone = f"ğŸ“ *Phone Number:* {escape_markdown_v2(phone_number)}\n\n" if phone_number else ""
            email = f"âœ‰ï¸ *Email:* {escape_markdown_v2(profile_data[8])}\n\n" if profile_data[8] else ""

