async def handle_payment_screenshot(update, context):
    logger.debug(f"Context user data: {context.user_data}")

    # Set up the Back button keyboard
    back_keyboard = ReplyKeyboardMarkup(
        [["ğŸ”™ Back"]],
        resize_keyboard=True
    )

